scripts.postResponse:
if (rq.response.code === 201) {
    const response = rq.response.json();
    if (response.success && response.data && response.data.userId) {
        rq.environment.set('adminUserId', response.data.userId);
        console.log('‚úÖ', response.message);
        console.log('Admin user registered successfully with ID:', response.data.userId);
        console.log('üìß Emails sent automatically:');
        console.log('   - ‚ú® Welcome email sent to:', response.data.email);
        console.log('   - üìß Notification email sent to main admin');
    } else {
        console.error('‚ùå Unexpected response format:', response);
    }
} else if (rq.response.code === 400 || rq.response.code === 403) {
    const error = rq.response.json();
    console.error('‚ùå', error.message || 'Request failed');
    if (error.errors) {
        console.error('Validation errors:', error.errors);
    }
}
