scripts.postResponse:
if (rq.response.code === 200) {
    const response = rq.response.json();
    if (response.success) {
        console.log('✅', response.message);
    }
} else if (rq.response.code === 400 || rq.response.code === 401 || rq.response.code === 403) {
    const error = rq.response.json();
    console.error('❌', error.message || 'Request failed');
    if (error.errors) {
        console.error('Validation errors:', error.errors);
    }
}

