scripts.postResponse:
if (rq.response.code === 200) {
    var jsonData = rq.response.json();
    if (jsonData.resetToken) {
        rq.environment.set('resetToken', jsonData.resetToken);
        var email = rq.request.body.urlencoded ? rq.request.body.urlencoded.get('email') : (rq.request.body.raw ? JSON.parse(rq.request.body.raw).email : '');
        if (email) {
            rq.environment.set('userEmail', email);
        }
        console.log('Reset Token saved:', jsonData.resetToken);
    }
}
